import { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

export default function BookDetails() {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const res = await axios.get(`/api/books/${_id}`);
        setBook(res.data);
        setLoading(false);
      } catch (err) {
        toast.error('Failed to fetch book details');
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  const addToCart = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        toast.error('Please login to add items to cart');
        return;
      }

      await axios.post(
        '/api/cart',
        { bookId: book._id, quantity },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }          
        }
      );
      toast.success('Added to cart');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Failed to add to cart');
    }
  };

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  if (!book) {
    return <div className="text-center py-8">Book not found</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="md:flex">
          <div className="md:w-1/3 p-4">
            <div className="h-64 bg-gray-200 flex items-center justify-center">
              {book.thumbnail ? (
                <img src={book.thumbnail} alt={book.title} className="h-full object-cover" />
              ) : (
                <span className="text-gray-500">No image available</span>
              )}
            </div>
          </div>
          <div className="md:w-2/3 p-6">
            <h1 className="text-3xl font-bold mb-2">{book.title}</h1>
            <p className="text-gray-600 text-xl mb-4">by {book.authors.join(', ')}</p>
            <p className="text-2xl font-bold text-blue-600 mb-4">${book.price.toFixed(2)}</p>
            <p className="text-gray-700 mb-6">{book.description}</p>
            
            <div className="flex items-center mb-6">
              <label className="mr-2">Quantity:</label>
              <select
                value={quantity}
                onChange={(e) => setQuantity(parseInt(e.target.value))}
                className="border rounded px-2 py-1"
              >
                {[...Array(10).keys()].map((num) => (
                  <option key={num + 1} value={num + 1}>
                    {num + 1}
                  </option>
                ))}
              </select>
              <span className="ml-4 text-gray-600">
                {book.stock > 0 ? `${book.stock} available` : 'Out of stock'}
              </span>
            </div>

            <button
              onClick={addToCart}
              disabled={book.stock === 0}
              className={`px-4 py-2 rounded-lg ${book.stock === 0 ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-500 hover:bg-blue-700 text-white'}`}
            >
              {book.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
