import { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { Link } from 'react-router-dom';

export default function Profile() {
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);
  const [passwordMode, setPasswordMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const [ordersRes, userRes] = await Promise.all([
          axios.get('/api/orders', {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get('/api/profile/me', {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);
        setOrders(ordersRes.data);
        setUser(userRes.data);
        setFormData({
          name: userRes.data.name,
          email: userRes.data.email,
        });
      } catch (err) {
        toast.error(err.response?.data?.msg || 'Failed to fetch data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const formatPrice = (price) =>
    typeof price === 'number' ? price.toFixed(2) : '0.00';

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePasswordChange = (e) => {
    setPasswordData({
      ...passwordData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.put(
        '/api/profile',
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUser(res.data);
      setEditMode(false);
      toast.success('Profile updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Failed to update profile');
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error('New passwords do not match');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      await axios.put(
        '/api/profile/password',
        {
          currentPassword: passwordData.currentPassword,
          newPassword: passwordData.newPassword
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setPasswordMode(false);
      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });
      toast.success('Password updated successfully');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Failed to update password');
    }
  };

  if (loading) {
    return (
      <div className="text-center py-8 text-vintage-brown font-serif">
        <i className="fas fa-spinner fa-spin text-2xl mb-2"></i>
        <p>Loading your profile...</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 bg-vintage-parchment min-h-screen font-serif text-vintage-dark">
      <div className="flex justify-between items-start mb-8 flex-wrap gap-4">
        <h2 className="text-3xl font-bold border-b-4 border-vintage-gold inline-block pb-1">
          <i className="fas fa-user mr-2"></i>
          Your Profile
        </h2>
        <div className="flex gap-2">
          <button
            onClick={() => {
              setEditMode(!editMode);
              setPasswordMode(false);
            }}
            className={`px-4 py-2 rounded-lg border-2 border-vintage-brown shadow-md transition-colors duration-300 ${
              editMode 
                ? 'bg-vintage-red hover:bg-vintage-dark text-white'
                : 'bg-vintage-light hover:bg-vintage-gold text-vintage-brown'
            }`}
          >
            {editMode ? (
              <>
                <i className="fas fa-times mr-2"></i>
                Cancel
              </>
            ) : (
              <>
                <i className="fas fa-edit mr-2"></i>
                Edit Profile
              </>
            )}
          </button>
          <button
            onClick={() => {
              setPasswordMode(!passwordMode);
              setEditMode(false);
            }}
            className={`px-4 py-2 rounded-lg border-2 border-vintage-brown shadow-md transition-colors duration-300 ${
              passwordMode 
                ? 'bg-vintage-red hover:bg-vintage-dark text-white'
                : 'bg-vintage-light hover:bg-vintage-gold text-vintage-brown'
            }`}
          >
            {passwordMode ? (
              <>
                <i className="fas fa-times mr-2"></i>
                Cancel
              </>
            ) : (
              <>
                <i className="fas fa-key mr-2"></i>
                Change Password
              </>
            )}
          </button>
        </div>
      </div>

      {/* Profile Edit Section */}
      {editMode ? (
        <div className="bg-vintage-light border-2 border-vintage-brown rounded-lg shadow-lg p-6 mb-8 hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-bold mb-4 text-vintage-brown border-b-2 border-vintage-gold pb-2">
            <i className="fas fa-user-edit mr-2"></i>
            Edit Profile
          </h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-semibold mb-1 text-vintage-dark">
                <i className="fas fa-signature mr-2"></i>
                Full Name
              </label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-vintage-brown rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-vintage-lightest font-serif"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold mb-1 text-vintage-dark">
                <i className="fas fa-envelope mr-2"></i>
                Email Address
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                className="w-full px-3 py-2 border border-vintage-brown rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-vintage-lightest font-serif"
              />
            </div>

            <div className="flex justify-end space-x-4 pt-4">
              <button
                type="button"
                onClick={() => setEditMode(false)}
                className="bg-vintage-light hover:bg-vintage-red text-vintage-dark px-4 py-2 rounded-lg border-2 border-vintage-brown shadow-md transition-colors duration-300"
              >
                <i className="fas fa-times mr-2"></i>
                Cancel
              </button>
              <button
                type="submit"
                className="bg-vintage-green hover:bg-vintage-dark text-white px-4 py-2 rounded-lg shadow-md transition-colors duration-300"
              >
                <i className="fas fa-save mr-2"></i>
                Save Changes
              </button>
            </div>
          </form>
        </div>
      ) : passwordMode ? (
        <div className="bg-vintage-light border-2 border-vintage-brown rounded-lg shadow-lg p-6 mb-8 hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-bold mb-4 text-vintage-brown border-b-2 border-vintage-gold pb-2">
            <i className="fas fa-key mr-2"></i>
            Change Password
          </h3>
          <form onSubmit={handlePasswordSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-semibold mb-1 text-vintage-dark">
                <i className="fas fa-lock mr-2"></i>
                Current Password
              </label>
              <input
                type="password"
                name="currentPassword"
                value={passwordData.currentPassword}
                onChange={handlePasswordChange}
                required
                className="w-full px-3 py-2 border border-vintage-brown rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-vintage-lightest font-serif"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold mb-1 text-vintage-dark">
                <i className="fas fa-lock-open mr-2"></i>
                New Password
              </label>
              <input
                type="password"
                name="newPassword"
                value={passwordData.newPassword}
                onChange={handlePasswordChange}
                required
                minLength="6"
                className="w-full px-3 py-2 border border-vintage-brown rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-vintage-lightest font-serif"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold mb-1 text-vintage-dark">
                <i className="fas fa-lock mr-2"></i>
                Confirm New Password
              </label>
              <input
                type="password"
                name="confirmPassword"
                value={passwordData.confirmPassword}
                onChange={handlePasswordChange}
                required
                minLength="6"
                className="w-full px-3 py-2 border border-vintage-brown rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-vintage-lightest font-serif"
              />
            </div>

            <div className="flex justify-end space-x-4 pt-4">
              <button
                type="button"
                onClick={() => setPasswordMode(false)}
                className="bg-vintage-light hover:bg-vintage-red text-vintage-dark px-4 py-2 rounded-lg border-2 border-vintage-brown shadow-md transition-colors duration-300"
              >
                <i className="fas fa-times mr-2"></i>
                Cancel
              </button>
              <button
                type="submit"
                className="bg-vintage-green hover:bg-vintage-dark text-white px-4 py-2 rounded-lg shadow-md transition-colors duration-300"
              >
                <i className="fas fa-save mr-2"></i>
                Change Password
              </button>
            </div>
          </form>
        </div>
      ) : (
        <div className="bg-vintage-light border-2 border-vintage-brown rounded-lg shadow-lg p-6 mb-8 hover:shadow-xl transition-shadow duration-300">
          <h3 className="text-2xl font-bold mb-4 text-vintage-brown border-b-2 border-vintage-gold pb-2">
            <i className="fas fa-user-circle mr-2"></i>
            Profile Information
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="text-lg font-semibold text-vintage-dark mb-2">
                <i className="fas fa-signature mr-2"></i>
                Name
              </h4>
              <p className="text-vintage-brown">{user?.name}</p>
            </div>
            <div>
              <h4 className="text-lg font-semibold text-vintage-dark mb-2">
                <i className="fas fa-envelope mr-2"></i>
                Email
              </h4>
              <p className="text-vintage-brown">{user?.email}</p>
            </div>
            <div>
              <h4 className="text-lg font-semibold text-vintage-dark mb-2">
                <i className="fas fa-calendar-alt mr-2"></i>
                Member Since
              </h4>
              <p className="text-vintage-brown">
                {new Date(user?.createdAt).toLocaleDateString('en-US', {
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric',
                })}
              </p>
            </div>
            <div>
              <h4 className="text-lg font-semibold text-vintage-dark mb-2">
                <i className="fas fa-shopping-bag mr-2"></i>
                Total Orders
              </h4>
              <p className="text-vintage-brown">{orders.length}</p>
            </div>
          </div>
        </div>
      )}

      {/* Order History Section */}
      <h2 className="text-3xl font-bold mb-6 border-b-4 border-vintage-gold inline-block pb-1">
        <i className="fas fa-history mr-2"></i>
        Your Orders
      </h2>

      {orders.length === 0 ? (
        <div className="text-center py-8">
          <i className="fas fa-book-open text-4xl text-vintage-brown mb-4"></i>
          <p className="mb-4 text-vintage-brown text-xl">You have no orders yet</p>
          <Link
            to="/"
            className="bg-vintage-red hover:bg-vintage-green text-white px-6 py-2 rounded-lg shadow-md transition-colors duration-300 inline-flex items-center"
          >
            <i className="fas fa-shopping-basket mr-2"></i>
            Start Shopping
          </Link>
        </div>
      ) : (
        <div className="space-y-6">
          {orders.map((order) => (
            <div
              key={order._id}
              className="bg-vintage-light border-2 border-vintage-brown rounded-lg shadow-lg p-6 hover:shadow-xl transition-shadow duration-300"
            >
              {/* ... (your existing order display code) ... */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
