import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    toast.success('Logged out successfully');
    navigate('/login');
  };

  return (
    <nav className="bg-vintage-dark text-vintage-light p-4 shadow-lg border-b border-vintage-brown">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="flex items-center space-x-3 vintage-text-glow">
          <i className="fas fa-book-open text-2xl text-vintage-light"></i>
          <span className="text-2xl font-bold font-serif">Vintage Bookshop</span>
        </Link>
        
        <div className="flex space-x-6">
          <Link to="/" className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors">
            <i className="fas fa-home"></i>
            <span>Home</span>
          </Link>
          
          {token ? (
            <>
              <Link to="/cart" className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors relative">
                <i className="fas fa-shopping-cart"></i>
                <span>Cart</span>
                {/* Cart badge would go here */}
              </Link>
              
              <Link to="/profile" className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors">
                <i className="fas fa-user"></i>
                <span>Profile</span>
              </Link>
              
              <button 
                onClick={handleLogout} 
                className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors"
              >
                <i className="fas fa-sign-out-alt"></i>
                <span>Logout</span>
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors">
                <i className="fas fa-sign-in-alt"></i>
                <span>Login</span>
              </Link>
              
              <Link to="/register" className="flex items-center space-x-2 vintage-text-glow hover:text-vintage-brown transition-colors">
                <i className="fas fa-user-plus"></i>
                <span>Register</span>
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
