import { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { Link } from 'react-router-dom';

export default function Profile() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/orders', {
          headers: {
            Authorization: `Bearer ${token}`
          }          
        });
        setOrders(res.data);
        setLoading(false);
      } catch (err) {
        toast.error('Failed to fetch orders');
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  const formatPrice = (price) => {
    return price?.toFixed ? price.toFixed(2) : '0.00';
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-2xl font-bold mb-6">Your Orders</h2>
      {orders.length === 0 ? (
        <div className="text-center py-8">
          <p className="mb-4">You have no orders yet</p>
          <Link
            to="/"
            className="bg-blue-500 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
          >
            Start Shopping
          </Link>
        </div>
      ) : (
        <div className="space-y-6">
          {orders.map((order) => (
            <div key={order._id} className="bg-white rounded-lg shadow-md overflow-hidden">
              <div className="p-4 border-b border-gray-200">
                <div className="flex justify-between items-center">
                  <h3 className="font-bold">Order ID: {order._id}</h3>
                  <p className="text-gray-600">
                    {new Date(order.createdAt).toLocaleDateString()}
                  </p>
                </div>
                <div className="mt-2 grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div>
                    <p className="text-sm text-gray-500">Status</p>
                    <p className="font-medium">
                      {order.isDelivered
                        ? `Delivered on ${new Date(order.deliveredAt).toLocaleDateString()}`
                        : 'Processing'}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Payment</p>
                    <p className="font-medium">
                      {order.isPaid
                        ? `Paid on ${new Date(order.paidAt).toLocaleDateString()}`
                        : 'Not Paid'}
                    </p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Total</p>
                    <p className="font-medium">${formatPrice(order.totalPrice)}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Items</p>
                    <p className="font-medium">{order.items.length}</p>
                  </div>
                </div>
              </div>
              <div className="p-4">
                <h4 className="font-bold mb-2">Order Items</h4>
                <div className="space-y-4">
                  {order.items.map((item) => (
                    <div key={item._id} className="flex items-center">
                      <div className="w-16 h-16 bg-gray-200 flex-shrink-0">
                        {item.book.thumbnail ? (
                          <img
                            src={item.book.thumbnail}
                            alt={item.book.title}
                            className="w-full h-full object-cover"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center text-gray-500">
                            No image
                          </div>
                        )}
                      </div>
                      <div className="ml-4 flex-grow">
                        <Link
                          to={`/books/${item.book._id}`}
                          className="font-medium hover:text-blue-600"
                        >
                          {item.book.title}
                        </Link>
                        <p className="text-gray-600 text-sm">
                          {item.quantity} x ${formatPrice(item.price)}
                        </p>
                      </div>
                      <div className="font-medium">
                        ${formatPrice((item.quantity || 0) * (item.price || 0))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
