import { useState } from 'react';

export default function CustomDropdown({ label, options, value, onChange }) {
  const [open, setOpen] = useState(false);

  return (
    <div className="relative w-full">
      {label && (
        <label className="block text-sm font-medium text-vintage-dark mb-1">{label}</label>
      )}
      <button
        onClick={() => setOpen(!open)}
        className="w-full px-3 py-2 border border-vintage-brown rounded-lg bg-vintage-light text-vintage-dark text-left"
      >
        {options.find(opt => opt.value === value)?.label || 'Select...'}
      </button>
      {open && (
        <ul className="absolute z-50 w-full mt-1 bg-vintage-light border border-vintage-brown rounded-lg shadow-lg max-h-60 overflow-auto">
          {options.map((option) => (
            <li
              key={option.value}
              onClick={() => {
                onChange(option.value);
                setOpen(false);
              }}
              className="px-4 py-2 hover:bg-vintage-brown hover:text-white cursor-pointer"
            >
              {option.label}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
