import { useState, useRef, useEffect } from 'react';

export default function CustomDropdown({ options, value, onChange }) {
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef();
  const listRef = useRef();

  const selectedOption = options.find(opt => opt.value === value);

  const toggleDropdown = () => setIsOpen(prev => !prev);
  const handleSelect = (val) => {
    onChange(val);
    setIsOpen(false);
  };

  // Calculate max height based on viewport height and option count
  const calculateMaxHeight = () => {
    const optionHeight = 40; // Approximate height of each option in pixels
    const maxVisibleOptions = 5;
    const mobileBuffer = 32; // Extra space from screen edges
    
    // Show all options if there are 5 or fewer, otherwise show 5 with scrolling
    if (options.length <= maxVisibleOptions) {
      return `${options.length * optionHeight}px`;
    }
    
    // On mobile, ensure dropdown doesn't exceed viewport height
    if (window.innerWidth < 640) { // Tailwind's 'sm' breakpoint
      const maxMobileHeight = window.innerHeight - mobileBuffer;
      return `${Math.min(maxVisibleOptions * optionHeight, maxMobileHeight)}px`;
    }
    
    return `${maxVisibleOptions * optionHeight}px`;
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div ref={dropdownRef} className="relative w-full max-w-xs">
      <button
        onClick={toggleDropdown}
        className="w-full px-4 py-2 bg-vintage-light border border-vintage-brown rounded-md text-vintage-dark font-semibold text-sm sm:text-base flex justify-between items-center"
      >
        <span>{selectedOption?.label || "Select an option"}</span>
        <svg
          className={`w-4 h-4 ml-2 transition-transform ${isOpen ? 'rotate-180' : ''}`}
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
        </svg>
      </button>

      {isOpen && (
        <ul 
          ref={listRef}
          style={{ maxHeight: calculateMaxHeight() }}
          className="absolute left-0 right-0 mt-1 bg-vintage-light border border-vintage-brown rounded-md shadow-md z-10 overflow-y-auto no-scrollbar"
        >
          {options.map((opt) => (
            <li
              key={opt.value}
              onClick={() => handleSelect(opt.value)}
              className="px-4 py-2 hover:bg-vintage-gold hover:text-white cursor-pointer"
            >
              {opt.label}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
