import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

export default function Cart() {
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      const token = localStorage.getItem('token');
      const res = await axios.get('/api/cart', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setCart(res.data);
      setLoading(false);
    } catch (err) {
      toast.error('Failed to fetch cart');
      setLoading(false);
    }
  };

  const updateQuantity = async (itemId, newQuantity) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(
        `/api/cart/${itemId}`,
        { quantity: newQuantity },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchCart();
      toast.success('Cart updated');
    } catch (err) {
      toast.error('Failed to update cart');
    }
  };

  const removeItem = async (itemId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/cart/${itemId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      fetchCart();
      toast.success('Item removed from cart');
    } catch (err) {
      toast.error('Failed to remove item');
    }
  };

  if (loading) {
    return (
      <div className="text-center py-8 font-serif text-vintage-brown">
        <i className="fas fa-box-open text-2xl animate-pulse mb-2"></i>
        <p>Loading your treasures...</p>
      </div>
    );
  }

  if (!cart || cart.items.length === 0) {
    return (
      <div className="container mx-auto px-4 py-8 text-center font-serif text-vintage-brown">
        <h2 className="text-2xl font-bold mb-4">Your Cart is Empty</h2>
        <Link
          to="/"
          className="bg-vintage-green hover:bg-vintage-dark text-white px-4 py-2 rounded-lg shadow-glow transition inline-flex items-center"
        >
          <i className="fas fa-arrow-left mr-2"></i>
          Continue Browsing
        </Link>
      </div>
    );
  }

  const subtotal = cart.items.reduce(
    (sum, item) => sum + item.book.price * item.quantity,
    0
  );

  return (
    <div className="container bg-[] mx-auto px-4 py-8 font-serif text-vintage-dark">
      <h2 className="text-3xl font-bold mb-6 text-center border-b-4 border-vintage-gold inline-block pb-1">
        <i className="fas fa-shopping-cart mr-2"></i>
        Your Cart
      </h2>

      <div className="bg-vintage-parchment rounded-xl shadow-glow p-4">
        <div className="divide-y divide-vintage-light">
          {cart.items.map((item) => (
            <div key={item._id} className="py-4 flex items-center">
              <div className="w-24 h-24 bg-vintage-light flex-shrink-0 rounded-lg overflow-hidden shadow-md border border-vintage-brown">
                {item.book.thumbnail ? (
                  <img
                    src={item.book.thumbnail}
                    alt={item.book.title}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-vintage-brown">
                    <i className="fas fa-book text-xl"></i>
                  </div>
                )}
              </div>

              <div className="ml-4 flex-grow">
                <h3 className="font-bold text-lg text-vintage-brown">
                  <Link to={`/books/${item.book._id}`} className="hover:text-vintage-red transition">
                    {item.book.title}
                  </Link>
                </h3>
                <p className="text-sm text-vintage-dark italic">
                  by {item.book.authors.join(', ')}
                </p>
                <p className="text-vintage-green font-bold mt-1">
                  ${item.book.price.toFixed(2)}
                </p>

                <div className="mt-2 flex items-center">
                  <label className="mr-2 text-sm">Qty:</label>
                  <select
                    value={item.quantity}
                    onChange={(e) => updateQuantity(item._id, parseInt(e.target.value))}
                    className="border border-vintage-brown rounded px-2 py-1 bg-white text-sm"
                  >
                    {[...Array(10).keys()].map((num) => (
                      <option key={num + 1} value={num + 1}>
                        {num + 1}
                      </option>
                    ))}
                  </select>
                  <button
                    onClick={() => removeItem(item._id)}
                    className="ml-4 text-vintage-red hover:text-red-800 text-sm font-medium"
                  >
                    <i className="fas fa-trash-alt mr-1"></i>
                    Remove
                  </button>
                </div>
              </div>

              <div className="text-right font-bold text-vintage-dark">
                ${(item.book.price * item.quantity).toFixed(2)}
              </div>
            </div>
          ))}
        </div>

        <div className="pt-4 border-t border-vintage-light mt-4">
          <div className="flex justify-between items-center text-xl font-bold">
            <h3 className="text-vintage-brown">Subtotal</h3>
            <p className="text-vintage-dark">${subtotal.toFixed(2)}</p>
          </div>

          <div className="mt-4">
            <Link
              to="/checkout"
              className="block w-full bg-vintage-green hover:bg-vintage-dark text-white text-center font-bold py-2 px-4 rounded-lg transition shadow-md"
            >
              Proceed to Checkout
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}
