import { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';

export default function Checkout() {
  const [shippingAddress, setShippingAddress] = useState({
    address: '',
    city: '',
    postalCode: '',
    country: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('PayPal');
  const [cart, setCart] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('/api/cart', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setCart(res.data);
        setLoading(false);
      } catch (err) {
        toast.error('Failed to fetch cart');
        setLoading(false);
      }
    };

    fetchCart();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const orderData = { shippingAddress, paymentMethod };

      await axios.post('/api/orders', orderData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      toast.success('Order placed successfully');
      navigate('/profile');
    } catch (err) {
      toast.error(err.response?.data?.msg || 'Failed to place order');
    }
  };

  const handleChange = (e) => {
    setShippingAddress({
      ...shippingAddress,
      [e.target.name]: e.target.value
    });
  };

  if (loading) {
    return <div className="text-center py-8 font-serif text-vintage-brown">Loading checkout...</div>;
  }

  if (!cart || cart.items.length === 0) {
    return (
      <div className="container mx-auto px-4 py-8 text-center font-serif text-vintage-brown">
        <h2 className="text-2xl font-bold mb-4">Your Cart is Empty</h2>
        <Link
          to="/"
          className="bg-vintage-green hover:bg-vintage-dark text-white px-4 py-2 rounded-lg shadow-glow transition"
        >
          Continue Shopping
        </Link>
      </div>
    );
  }

  const subtotal = cart.items.reduce((sum, item) => sum + item.book.price * item.quantity, 0);
  const shippingPrice = subtotal > 100 ? 0 : 10;
  const taxPrice = Number((0.15 * subtotal).toFixed(2));
  const totalPrice = (subtotal + shippingPrice + taxPrice).toFixed(2);

  return (
    <div className="container mx-auto px-4 py-8 font-serif text-vintage-dark">
      <h2 className="text-3xl font-bold mb-6 text-center">Checkout</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <div className="bg-vintage-parchment rounded-xl shadow-glow p-6">
          <h3 className="text-xl font-bold mb-4 text-vintage-brown">Shipping</h3>
          <form onSubmit={handleSubmit}>
            {['address', 'city', 'postalCode', 'country'].map((field) => (
              <div className="mb-4" key={field}>
                <label className="block text-sm font-semibold mb-1 capitalize">{field.replace('Code', ' Code')}</label>
                <input
                  type="text"
                  name={field}
                  value={shippingAddress[field]}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-vintage-dark rounded-lg focus:outline-none focus:ring-2 focus:ring-vintage-green bg-white"
                />
              </div>
            ))}
            <h3 className="text-xl font-bold mb-4 text-vintage-brown">Payment Method</h3>
            <div className="mb-6 space-y-2">
              {['PayPal', 'CreditCard', 'CashOnDelivery'].map((method) => (
                <div key={method} className="flex items-center">
                  <input
                    type="radio"
                    id={method}
                    name="paymentMethod"
                    value={method}
                    checked={paymentMethod === method}
                    onChange={() => setPaymentMethod(method)}
                    className="mr-2"
                  />
                  <label htmlFor={method} className="capitalize">{method.replace('OnDelivery', ' on Delivery')}</label>
                </div>
              ))}
            </div>
            <button
              type="submit"
              className="w-full bg-vintage-green hover:bg-vintage-dark text-white font-bold py-2 px-4 rounded-lg shadow transition"
            >
              Place Order
            </button>
          </form>
        </div>

        <div className="bg-vintage-parchment rounded-xl shadow-glow p-6">
          <h3 className="text-xl font-bold mb-4 text-vintage-brown">Order Summary</h3>
          <div className="divide-y divide-vintage-light">
            {cart.items.map((item) => (
              <div key={item._id} className="py-4 flex justify-between">
                <div className="flex items-center">
                  <div className="w-16 h-16 bg-vintage-light mr-4 rounded overflow-hidden">
                    {item.book.thumbnail ? (
                      <img
                        src={item.book.thumbnail}
                        alt={item.book.title}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-vintage-dark text-sm">
                        No image
                      </div>
                    )}
                  </div>
                  <div>
                    <h4 className="font-medium text-vintage-dark">{item.book.title}</h4>
                    <p className="text-vintage-brown text-sm">
                      {item.quantity} x ${item.book.price.toFixed(2)}
                    </p>
                  </div>
                </div>
                <p className="font-medium text-vintage-dark">
                  ${(item.book.price * item.quantity).toFixed(2)}
                </p>
              </div>
            ))}
          </div>
          <div className="border-t border-vintage-light mt-4 pt-4">
            <div className="flex justify-between mb-2">
              <span>Subtotal</span>
              <span>${subtotal.toFixed(2)}</span>
            </div>
            <div className="flex justify-between mb-2">
              <span>Shipping</span>
              <span>${shippingPrice.toFixed(2)}</span>
            </div>
            <div className="flex justify-between mb-2">
              <span>Tax (15%)</span>
              <span>${taxPrice}</span>
            </div>
            <div className="flex justify-between font-bold text-lg mt-4">
              <span>Total</span>
              <span>${totalPrice}</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
